{{
  (() => {
    // 1️⃣ Trae el valor desde la query o deja vacío
    const raw = q_cliente_detalle.data?.[0]?.tag || "";
    if (!raw) return "—";

    // 2️⃣ Normaliza (elimina espacios y pone mayúsculas)
    const t = raw.trim().toUpperCase().replace(/\s+/g, "_");

    // 3️⃣ Mapeo de equivalencias
    const map = {
      BONO_FAMILIA: "Bono Familia",
      BONO_VIP_A: "Bono VIP A",
      BONO_VIP_B: "Bono VIP B",
      BONO_VIP_C: "Bono VIP C",
      BONO_TARJETA: "Bono Tarjeta",
      VIP: "VIP",
      FRIEND: "Amigo",
      NEW: "Nuevo",
      ESPORADICO: "Esporádico",
      NONE: "—",
    };

    // 4️⃣ Devuelve la versión amigable
    return map[t] || raw;
  })()
}}
