@charset "UTF-8";
:root {
  --card-bg: #ffffff;
  --card-bdr: #e5e7eb;
  --text: #111827;
  --muted: #6b7280;
  --blue: #3b82f6;
  --gray-pill: #f3f4f6;
  --green: #10b981;
}

* {
  box-sizing: border-box;
}

.card {
  background: var(--card-bg);
  border: 1px solid var(--card-bdr);
  border-radius: 14px;
  padding: 16px;
  color: var(--text);
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 12px;
}

.header h3 {
  margin: 0;
  font-size: 16px;
  font-weight: 700;
}

.badges {
  display: inline-flex;
  gap: 8px;
}

.badge {
  display: inline-flex;
  align-items: center;
  padding: 6px 10px;
  border-radius: 999px;
  font-size: 12px;
  line-height: 1;
  border: 1px solid transparent;
}

.badge-gray {
  background: var(--gray-pill);
  color: var(--text);
}

.badge-blue {
  background: rgba(59, 130, 246, 0.12);
  color: var(--blue);
  border-color: rgba(59, 130, 246, 0.2);
}

.badge.done {
  background: rgba(16, 185, 129, 0.12);
  color: var(--green);
  border-color: rgba(16, 185, 129, 0.2);
}

/* GRID */
.grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr); /* normal: 10 slots (5x2) */
  gap: 12px;
}

/* si es VIP, el JS añade .vip al grid → 4 columnas */
.grid.vip {
  grid-template-columns: repeat(4, 1fr);
}

/* Slot */
.slot {
  display: grid;
  place-items: center;
  height: 56px;
  border-radius: 12px;
  border: 1px solid var(--card-bdr);
  background: #f9fafb;
  color: var(--muted);
  font-weight: 600;
  user-select: none;
  cursor: default;
  transition: transform 0.06s ease, background 0.2s ease, color 0.2s ease, border-color 0.2s ease;
}

.slot:hover {
  transform: translateY(-1px);
}

.slot.active {
  background: var(--blue);
  color: white;
  border-color: var(--blue);
}

.slot.done {
  background: #ecfdf5;
  color: var(--green);
  border-color: rgba(16, 185, 129, 0.35);
}

