// ===== Colores (mismo esquema) ======================================
const COLORS = {
  slotFilledBg: '#2F6FEB',
  slotFilledText: '#FFFFFF',
  slotEmptyBg: '#EEF2F7',
  slotEmptyText: '#8A93A5',
  badgeGrayBg: '#E5E7EB',
  badgeGrayText: '#374151',
  badgeGreenBg: '#D1FAE5',
  badgeGreenText: '#065F46',
  badgeBlueBg: '#DBEAFE',
  badgeBlueText: '#1E40AF'
};

// Utilidad texto seguro
const txt = (v, def = '') => v == null ? def : String(v);

// Dibuja "pastilla" (badge)
function paintBadge(el, text, completed, theme /*'green'|'blue'|'gray'*/) {
  if (!el) return;
  if (!text) {
    el.hidden = true;
    el.style.display = 'none';
    return;
  }
  el.hidden = false;
  el.style.display = 'inline-block';
  el.textContent = text;
  let bg = COLORS.badgeGrayBg;
  let fg = COLORS.badgeGrayText;
  if (theme === 'green') {
    bg = completed ? COLORS.badgeGreenBg : COLORS.badgeGrayBg;
    fg = completed ? COLORS.badgeGreenText : COLORS.badgeGrayText;
  } else if (theme === 'blue') {
    bg = completed ? COLORS.badgeBlueBg : COLORS.badgeGrayBg;
    fg = completed ? COLORS.badgeBlueText : COLORS.badgeGrayText;
  }
  el.style.background = bg;
  el.style.color = fg;
}

// Botón de slot
function createSlotButton(slot, model) {
  const btn = document.createElement('button');
  btn.className = 'slot';
  btn.textContent = slot.value;
  const filled = !!slot.filled;

  // estilos
  btn.style.background = filled ? COLORS.slotFilledBg : COLORS.slotEmptyBg;
  btn.style.color = filled ? COLORS.slotFilledText : COLORS.slotEmptyText;
  btn.style.border = '0';
  btn.style.borderRadius = '14px';
  btn.style.height = '64px';
  btn.style.fontWeight = '700';
  btn.style.fontSize = '18px';
  btn.style.cursor = filled ? 'default' : 'pointer';
  if (filled) {
    btn.style.opacity = '0.9';
    btn.style.pointerEvents = 'none'; // evita doble registro
  }

  // Click → registrar visita sólo si no está marcado
  if (!filled) {
    btn.addEventListener('click', () => {
      appsmith.triggerEvent('onAddVisit', {
        slot: slot.value,
        model
      });
    });
  }
  return btn;
}

// Genera slots si no llegan desde el modelo
function ensureSlots(current, total) {
  const arr = [];
  for (let i = 1; i <= total; i++) {
    arr.push({
      value: i,
      filled: i <= (current || 0)
    });
  }
  return arr;
}

// Render principal
function render(model) {
  const titleEl = document.getElementById('title');
  const badge50El = document.getElementById('badge50');
  const badgeFreeEl = document.getElementById('badgeFree');
  const gridEl = document.getElementById('grid');
  if (!titleEl || !badge50El || !badgeFreeEl || !gridEl) {
    console.warn('[custom] Faltan nodos: title, badge50, badgeFree, grid');
    return;
  }

  // === Datos del modelo ===
  const planName = txt(model.planName).toLowerCase();
  const tagName = txt(model.tag).toLowerCase();
  const currentVisits = Number(model.currentVisits || 0);
  const isVip = Boolean(model.isVip) || /vip/i.test(planName) || /vip/i.test(tagName);

  // total de casillas según VIP o no (si no llega, default 4/10)
  const totalSlots = Number(model.totalSlots || (isVip ? 4 : 10));

  // --- Textos adaptativos ---
  let headerText = '';
  let prize50 = null; // Eliminado (ya no se usa)
  let prizeFree = null;
  if (isVip) {
    const target = 4;
    headerText = currentVisits < target ? `VIP: ¡Solo ${target - currentVisits} visitas para el Gratis!` : '¡Premio VIP listo para canjear!';

    // Sólo premio Gratis (VIP)
    prizeFree = {
      text: `${target} para Servicio Gratis (VIP)`,
      completed: currentVisits >= target
    };
  } else {
    // Solo objetivo de GRATIS (10). Se elimina el 50% para siempre.
    const targetGratis = 10;
    if (currentVisits < targetGratis) {
      headerText = `¡Solo ${targetGratis - currentVisits} para Gratis!`;
    } else {
      headerText = '¡Premio Gratis listo para canjear!';
    }

    // Sin 50%: ocultamos esa pastilla y dejamos solo "Servicio Gratis"
    prize50 = null;
    prizeFree = {
      text: `${targetGratis} para Servicio Gratis`,
      completed: currentVisits >= targetGratis
    };
  }

  // --- Aplicar DOM ---
  titleEl.textContent = headerText;
  paintBadge(badge50El, '', false, 'green'); // Oculta el 50%
  paintBadge(badgeFreeEl, prizeFree?.text || '', !!(prizeFree && prizeFree.completed), 'blue');

  // --- Grilla de slots ---
  const slots = Array.isArray(model.slots) && model.slots.length ? model.slots : ensureSlots(currentVisits, totalSlots);
  gridEl.innerHTML = '';
  gridEl.style.display = 'grid';
  gridEl.style.gap = '16px';
  const cols = isVip ? 2 : Math.min(5, Math.max(2, totalSlots || slots.length || 10));
  gridEl.style.gridTemplateColumns = `repeat(${cols}, 1fr)`;
  slots.forEach(s => gridEl.appendChild(createSlotButton(s, model)));
}

// Ciclo de vida del Custom Widget
appsmith.onReady(() => {
  render(appsmith.model || {});
});
appsmith.onModelChange = newModel => {
  render(newModel || {});
};

