onClick:
{{
(async () => {
  const v = JS_BondPayHelper.validatePagoVipForm();
  if (!v.ok) { showAlert(v.errs.join("\n"), "error"); return; }

  const code = JS_BondPayHelper.genOtp();
  await storeValue("otp_code_plain", code);

  // Enviar con plantilla (reemplaza HX... por tu SID real)
  await wa_send.run({
    to: v.phoneE164,
    templateVars: { "1": code, "2": "10" },
    contentSidOverride: "HXxxxxxxxxxxxxxxxxxxxxxxxxxxxx"
  });

  showAlert("Código enviado por WhatsApp", "success");
  // Aquí muestras un input para el código y un botón "Confirmar OTP"
})()
}}
