:root {
  --chip-size:40px;
  --gap:8px;
  --radius:12px;
  --filled-bg:#3b82f6;
  --filled-fg:#fff;
  --empty-bg:#eee;
  --empty-fg:#666;
}

#strip {
  display: flex;
  gap: var(--gap);
  width: 100%;
  align-items: center;
  overflow-x: auto;
  padding: 6px 2px;
}

.chip {
  min-width: var(--chip-size);
  width: var(--chip-size);
  height: var(--chip-size);
  border-radius: var(--radius);
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  user-select: none;
  background: var(--empty-bg);
  color: var(--empty-fg);
  box-shadow: inset 0 0 0 1px #ddd;
}

.chip.filled {
  background: var(--filled-bg);
  color: var(--filled-fg);
  box-shadow: inset 0 0 0 2px rgba(255, 255, 255, 0.15), 0 1px 2px rgba(0, 0, 0, 0.12);
}

