function render(data) {
  const root = document.getElementById("strip");
  if (!root) return;
  const items = Array.isArray(data?.items) ? data.items : [];
  const size = Number(data?.size ?? 40);
  const gap = Number(data?.gap ?? 8);
  const radius = Number(data?.radius ?? 12);
  const filledBg = data?.filledColor ?? "#3b82f6";
  const filledFg = data?.filledTextColor ?? "#ffffff";
  const emptyBg = data?.emptyColor ?? "#eeeeee";
  const emptyFg = data?.emptyTextColor ?? "#666666";
  root.style.setProperty("--chip-size", `${size}px`);
  root.style.setProperty("--gap", `${gap}px`);
  root.style.setProperty("--radius", `${radius}px`);
  root.style.setProperty("--filled-bg", filledBg);
  root.style.setProperty("--filled-fg", filledFg);
  root.style.setProperty("--empty-bg", emptyBg);
  root.style.setProperty("--empty-fg", emptyFg);
  root.innerHTML = items.map(p => `<div class="chip ${p.filled ? "filled" : ""}" title="${p.n}">${p.n}</div>`).join("");
}
appsmith.onReady(() => {
  // primera pintura
  render(appsmith.data);

  // "reactividad" simple: repinta cuando cambie Data
  let last = "";
  function tick() {
    try {
      const cur = JSON.stringify(appsmith.data);
      if (cur !== last) {
        last = cur;
        render(appsmith.data);
      }
    } catch {}
    setTimeout(tick, 120); // 8–10 fps es suficiente
  }
  tick();
  return {
    dynamicHeight: false
  };
});

