isDisabled:
{{
  !(
    // 1) Email válido (sin espacios)
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test((InputEmail.text || '').trim())
    // 2) Contraseña actual no vacía
    && (InputPassActual.text || '').length >= 1
    // 3) Nueva contraseña con mínimo 8 caracteres (en ambos campos)
    && (InputPassNueva.text || '').length >= 8
    && (InputPassNueva2.text || '').length >= 8
    // 4) Las nuevas coinciden
    && (InputPassNueva.text === InputPassNueva2.text)
    // 5) La nueva es distinta a la actual
    && (InputPassNueva.text !== InputPassActual.text)
  )
}}
